import React, { Component } from 'react';
import { Link, Route } from 'react-router-dom'




class App extends Component {
  constructor (props) {
    super(props);

    this.state={
      userName:"",
      title:"",
      content:"",
      list:[]
    }
  }

    updateInput(key, value){
      this.setState({
        [key]:value

      })
    }

    addItem(){
      const userName = {
          id: 1 + Math.random(),
          value: this.state.userName.slice()
      };
      const title = {
        id: 1 + Math.random(),
        value: this.state.title.slice()
    };
    const content = {
        id: 1 + Math.random(),
        value: this.state.content.slice()
    };

      const list = [...this.state.list];

      list.push(userName, title, content);

      this.setState({
        list,
        userName:"",
        title:"",
        content:"",
      });

    }

    deleteItem (id) {
      const list = [...this.state.list];
      const updateList = list.filter(item => item.id !== id);

      this.setState({list: updateList});
    }

    render () {
      return (
          <div>

            <input
            type="text"
            placeholder="Username"
            value={this.state.userName}
            onChange={e=> this.updateInput("userName", e.target.value)}
            />
            <input
            type="text"
            placeholder="Title"
            value={this.state.title}
            onChange={e=> this.updateInput("title", e.target.value)}
            />
            <input
            type="text"
            placeholder="Content"
            value={this.state.content}
            onChange={e=> this.updateInput("content", e.target.value)}
            />
            <button onClick={()=> this.addItem()}>$</button>
            <br/>

          </div>
      );
    }
  }


export default App;
